@RestController
public class VisionController {

    private final ChatClient chatClient;

    public VisionController(ChatClient.Builder chatClientBuilder) {
        this.chatClient = chatClientBuilder
            .defaultOptions(OpenAiChatOptions.builder()
                .withModel("Qwen2-VL-72B-Instruct")
                .withMaxTokens(1000)
                .build())
            .build();
    }

    @PostMapping("/api/vision/analyze")
    public String analyzeImage(@RequestParam("file") MultipartFile file) throws IOException {
        // Convert image to base64
        byte[] imageBytes = file.getBytes();
        String base64Image = Base64.getEncoder().encodeToString(imageBytes);
        String imageUrl = "data:image/jpeg;base64," + base64Image;

        return chatClient.prompt()
            .user(userSpec -> userSpec
                .text("请描述这张图片")
                .media(MimeTypeUtils.IMAGE_JPEG, imageUrl))
            .call()
            .content();
    }

    @PostMapping("/api/vision/ocr")
    public String extractText(@RequestParam("file") MultipartFile file) throws IOException {
        byte[] imageBytes = file.getBytes();
        String base64Image = Base64.getEncoder().encodeToString(imageBytes);
        String imageUrl = "data:image/jpeg;base64," + base64Image;

        return chatClient.prompt()
            .user(userSpec -> userSpec
                .text("请提取图片中的所有文字内容")
                .media(MimeTypeUtils.IMAGE_JPEG, imageUrl))
            .call()
            .content();
    }
}

